('1763141252271', 'Partnerships', 'Partnerships', 'partnerships', '[\n    {\n        \"id\": \"partnership-hero-1\",\n        \"type\": \"partnership-hero\",\n        \"content\": {\n            \"title\": \"Strategic Partnerships\",\n            \"subtitle\": \"Building Tomorrow Together\",\n            \"description\": \"We collaborate with industry leaders to deliver innovative solutions that drive sustainable growth and create lasting impact.\",\n            \"buttonText\": \"Explore Opportunities\",\n            \"buttonLink\": \"/contact\",\n            \"buttonVariant\": \"primary\",\n            \"buttonIcon\": \"arrow-right\",\n            \"buttonAnimation\": \"slide\",\n            \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763451544113-189675.jpg\",\n            \"backgroundVideo\": \"\",\n            \"backgroundColor\": \"#021124\",\n            \"textColor\": \"#ffffff\",\n            \"titleColor\": \"#EF4130\",\n            \"subtitleColor\": \"#F3F4F6\",\n            \"alignment\": \"center\",\n            \"overlay\": true,\n            \"overlayOpacity\": 0.65,\n            \"overlayColor\": \"#000000\",\n            \"titleSize\": \"6xl\",\n            \"subtitleSize\": \"4xl\",\n            \"descriptionSize\": \"xl\",\n            \"animationEnabled\": true,\n            \"animationStyle\": \"fade\",\n            \"showScrollIndicator\": true,\n            \"height\": \"full\"\n        }\n    },\n    {\n        \"id\": \"partnership-philosophy-1\",\n        \"type\": \"partnership-philosophy\",\n        \"content\": {\n            \"title\": \"Our Partnership Philosophy\",\n            \"description\": \"We view every partner as an extension of our mission — contributing to intelligent, sustainable and high-performing environments. Every collaboration is built on:\",\n            \"backgroundColor\": \"#f7f7f7\",\n            \"titleColor\": \"#dc2626\",\n            \"textColor\": \"#0d0d0d\",\n            \"accentColor\": \"#dc2626\",\n            \"features\": [\n                {\n                    \"title\": \"Trust & Transparency\",\n                    \"icon\": \"ShieldCheck\"\n                },\n                {\n                    \"title\": \"Shared Values\",\n                    \"icon\": \"Globe\"\n                },\n                {\n                    \"title\": \"Mutual Growth\",\n                    \"icon\": \"Users\"\n                },\n                {\n                    \"title\": \"Commitment to Excellence\",\n                    \"icon\": \"Award\"\n                },\n                {\n                    \"title\": \"Long-term Relationship Building\",\n                    \"icon\": \"Handshake\"\n                }\n            ],\n            \"cardPadding\": \"py-4 px-6\",\n            \"animationEnabled\": true\n        }\n    },\n    {\n        \"id\": \"collaborating-with-purpose-1\",\n        \"type\": \"collaborating-with-purpose\",\n        \"content\": {\n            \"title\": \"Collaborating with Purpose. Delivering with Precision.\",\n            \"subtitle\": \"\",\n            \"description\": \"At RAUS, partnerships are more than collaborations — they are a shared commitment to shaping future-ready environments built on innovation, sustainability and excellence. Our alliances enable us to bring together diverse expertise, global perspectives and advanced technologies to deliver integrated solutions that exceed expectations.\\n\\nWe believe that when the right minds come together, extraordinary outcomes are possible.\",\n            \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763451574301-357142.jpg\",\n            \"backgroundVideo\": \"\",\n            \"backgroundColor\": \"#0b1220\",\n            \"titleColor\": \"#FFFFFF\",\n            \"subtitleColor\": \"#E5E7EB\",\n            \"textColor\": \"#E6EEF6\",\n            \"overlay\": true,\n            \"overlayColor\": \"#000000\",\n            \"overlayOpacity\": 0.6,\n            \"titleSize\": \"4xl\",\n            \"descriptionSize\": \"base\",\n            \"animationEnabled\": true,\n            \"animationStyle\": \"fade\",\n            \"alignment\": \"center\",\n            \"buttonText\": \"Contact Us\",\n            \"buttonLink\": \"/contact\",\n            \"accentColor\": \"#dc2626\",\n            \"features\": [\n                \"Sustainable Design\",\n                \"Global Reach\",\n                \"Advanced Technology\"\n            ],\n            \"blurIntensity\": 6\n        }\n    },\n    {\n        \"id\": \"our-partner-ecosystem-1\",\n        \"type\": \"our-partner-ecosystem\",\n        \"content\": {\n            \"title\": \"Our Partner Ecosystem\",\n            \"subtitle\": \"RAUS collaborates with a powerful network of global and regional partners who bring specialized knowledge, cutting-edge tools and execution excellence.\",\n            \"backgroundColor\": \"#ededed\",\n            \"titleColor\": \"#000000\",\n            \"textColor\": \"#000000\",\n            \"accentColor\": \"#EF4130\",\n            \"categories\": [\n                {\n                    \"title\": \"Global Technology Providers\",\n                    \"icon\": \"Globe\",\n                    \"description\": \"We align with leading innovators in BIM, IoT, AI analytics, digital twins and automation to enhance project precision and digital transformation.\",\n                    \"bullets\": [\n                        \"Intelligent modeling and visualization\",\n                        \"Real-time project analytics\",\n                        \"Predictive risk and performance modeling\",\n                        \"Smart infrastructure integration\"\n                    ]\n                },\n                {\n                    \"title\": \"Specialist Consultants\",\n                    \"icon\": \"Users\",\n                    \"description\": \"For every project, RAUS brings in domain experts who elevate quality, comfort and functionality.\",\n                    \"bullets\": [\n                        \"Acoustics & sound engineering\",\n                        \"Ergonomics & human-centered design\",\n                        \"Biophilic & wellness design\",\n                        \"Glare management & lighting\",\n                        \"Sustainable materials & circular design\"\n                    ]\n                },\n                {\n                    \"title\": \"Sustainability & Green Building Partners\",\n                    \"icon\": \"Leaf\",\n                    \"description\": \"Sustainability is a core pillar at RAUS and we collaborate with organizations leading advancements in green building and circular economy frameworks.\",\n                    \"bullets\": [\n                        \"LEED & green certification\",\n                        \"Circular economy systems\",\n                        \"Low-carbon material development\",\n                        \"Water and energy conservation\",\n                        \"Climate risk and ESG advisory\"\n                    ]\n                },\n                {\n                    \"title\": \"Construction & Fit-Out Contractors\",\n                    \"icon\": \"Tool\",\n                    \"description\": \"RAUS works with trusted construction experts who share our commitment to quality, safety and timely delivery.\",\n                    \"bullets\": [\n                        \"Precise execution of design intent\",\n                        \"Efficient coordination across trades\",\n                        \"Superior craftsmanship\",\n                        \"On-time milestones & transparent oversight\"\n                    ]\n                },\n                {\n                    \"title\": \"Public Sector & Regulatory Authorities\",\n                    \"icon\": \"FileText\",\n                    \"description\": \"RAUS maintains strong relationships with key government bodies and approval authorities—critical for navigating compliance, permitting and technical approvals.\",\n                    \"bullets\": [\n                        \"Faster authority coordination\",\n                        \"Efficient documentation & NOC processes\",\n                        \"Regulatory alignment from concept to completion\"\n                    ]\n                }\n            ],\n            \"animationEnabled\": true,\n            \"cardBackgroundColor\": \"#ffffff\"\n        }\n    },\n    {\n        \"id\": \"why-choose-raus\",\n        \"type\": \"WhyChooseRaus\",\n        \"content\": {\n            \"title\": \"Why Partners Choose RAUS\",\n            \"description\": \"Together, we deliver impact that is scalable, future-proof and human-centered.\",\n            \"bullets\": [\n                \"Integrated expertise across design, engineering, sustainability & digital transformation\",\n                \"A collaborative, transparent working model\",\n                \"Advanced digital tools that reduce risks and accelerate decision-making\",\n                \"A strong ethical and governance framework\",\n                \"A commitment to innovation and sustainability in every project\"\n            ],\n            \"accentColor\": \"#dc2626\",\n            \"backgroundColor\": \"#F5F7FA\",\n            \"animationEnabled\": true,\n            \"animationStyle\": \"fade\",\n            \"alignment\": \"center\"\n        }\n    },\n    {\n        \"id\": \"mobility-partnership-intro-1\",\n        \"type\": \"mobility-partnership-intro\",\n        \"content\": {\n            \"accentColor\": \"#EF4130\"\n        }\n    },\n    {\n        \"id\": \"mobility-partners-list-1\",\n        \"type\": \"mobility-partners-list\",\n        \"content\": {\n            \"backgroundColor\": \"#363636\",\n            \"cardBackgroundColor\": \"#EF4130\",\n            \"accentColor\": \"#FFFFFF\",\n            \"textColor\": \"#FFFFFF\",\n            \"partnersCardBackgroundColor\": \"#EF4130\",\n            \"eyebrowIconColor\": \"#EF4130\"\n        }\n    },\n    {\n        \"id\": \"call-to-action\",\n        \"type\": \"CallToAction\",\n        \"content\": {\n            \"title\": \"Partner With Us\",\n            \"description\": \"Join our mission to reshape built environments through innovation, purpose and integrity. Whether you’re a technology leader, sustainability consultant, contractor, or regulatory collaborator, RAUS welcomes partners who share our vision for smarter, greener and more resilient infrastructure.\",\n            \"buttonText\": \"Become a Partner\",\n            \"buttonLink\": \"/contact\",\n            \"accentColor\": \"#dc2626\",\n            \"backgroundColor\": \"#F0FDF4\",\n            \"animationStyle\": \"fade\",\n            \"alignment\": \"center\"\n        }\n    }\n]', '{\"title\":\"Partnership\",\"description\":\"\"}', '1764155028441', '2025-11-14 22:57:32', '2025-11-26 16:33:48', '[]', '[]'),
