('1763141252537', 'Digital Transformation', 'Digital Transformation', 'digital-transformation', '[\n    {\n        \"id\": \"digital-transformation-hero-banner\",\n        \"type\": \"DigitalTransformationHeroBanner\",\n        \"content\": {\n            \"title\": \"Digital Transformation\",\n            \"subtitle\": \"Reimagining the Future of Business\",\n            \"description\": \"Leverage cutting-edge technologies to accelerate innovation, streamline operations and deliver unparalleled customer experiences in the digital era.\",\n            \"buttonText\": \"Start Your Journey\",\n            \"buttonLink\": \"#contact\",\n            \"buttonVariant\": \"gradient\",\n            \"buttonAnimation\": \"glow\",\n            \"secondaryButtonText\": \"See Case Studies\",\n            \"secondaryButtonLink\": \"#case-studies\",\n            \"secondaryButtonVariant\": \"outline\",\n            \"secondaryButtonAnimation\": \"pulse\",\n            \"backgroundColor\": \"#0f172a\",\n            \"accentColor\": \"#DC2626\",\n            \"textColor\": \"#e2e8f0\",\n            \"overlayOpacity\": 0.6,\n            \"overlayColor\": \"#020617\",\n            \"alignment\": \"center\",\n            \"height\": \"full\",\n            \"overlay\": true,\n            \"animationEnabled\": true,\n            \"titleColor\": \"#ffffff\",\n            \"subtitleColor\": \"#00d4ff\",\n            \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763535811998-861431.jpeg\",\n            \"backgroundVideo\": \"\",\n            \"imageUrl\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763535811998-861431.jpeg\",\n            \"videoUrl\": \"\"\n        }\n    },\n    {\n        \"id\": \"digital-transformation-paragraph-1\",\n        \"type\": \"ParagraphSectionFirst\",\n        \"content\": {\n            \"title\": \"Transforming Spaces Into Intelligent, Connected, Future-Ready Ecosystems edit \",\n            \"paragraphs\": [\n                \"At RAUS, Digital Transformation is more than integrating technology—it’s about reshaping how buildings operate, how people interact with spaces and how organizations unlock long-term performance. edit \",\n                \"We bridge architecture, engineering, data and smart technologies to create intelligent environments that are adaptive, efficient and built for tomorrow. edit\",\n                \"From smart buildings and IoT systems to digital twins, automation and AI-driven project delivery, our digital solutions elevate every stage of the built environment lifecycle—design, construction, operations and maintenance. edit\"\n            ],\n            \"accentColor\": \"#dc2626\",\n            \"backgroundColor\": \"#f8fafc\",\n            \"animationStyle\": \"fade\",\n            \"alignment\": \"left\"\n        }\n    },\n    {\n        \"id\": \"digital-transformation-approach\",\n        \"type\": \"DigitalTransformationApproach\",\n        \"content\": {\n            \"icon\": \"\",\n            \"mainTitle\": \"Our Digital Transformation Approach\",\n            \"subtitle\": \"We combine deep industry insight with advanced technology to ensure every project is smarter, greener and more connected. Our approach focuses on:\",\n            \"approaches\": [\n                {\n                    \"number\": \"1\",\n                    \"title\": \"Technology-Enabled Design & Planning\",\n                    \"description\": \"By integrating technology early, we ensure clarity, reduce rework and accelerate project timelines.\",\n                    \"features\": [\n                        \"Intelligent BIM modeling for accuracy and coordination\",\n                        \"Predictive simulations using digital twins\",\n                        \"Real-time stakeholder visualization using VR/AR\",\n                        \"Data-driven decision support for cost, quality & efficiency\"\n                    ],\n                    \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763547757485-719604.jpeg\"\n                },\n                {\n                    \"number\": \"2\",\n                    \"title\": \"Smart Construction & Execution\",\n                    \"description\": \"Leverage IoT sensors, drones and AI-powered analytics for real-time monitoring and optimization.\",\n                    \"features\": [\n                        \"Automated progress tracking and reporting\",\n                        \"IoT-enabled safety and equipment monitoring\",\n                        \"Drone surveys for site analysis\",\n                        \"AI-driven quality control and defect detection\"\n                    ],\n                    \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763547757485-719604.jpeg\"\n                },\n                {\n                    \"number\": \"3\",\n                    \"title\": \"Intelligent Operations & Maintenance\",\n                    \"description\": \"Transform building operations with smart systems that optimize energy, comfort and performance.\",\n                    \"features\": [\n                        \"Smart building management systems (BMS)\",\n                        \"Predictive maintenance using AI and IoT\",\n                        \"Energy optimization and sustainability tracking\",\n                        \"Occupant experience enhancement through automation\"\n                    ],\n                    \"backgroundImage\": \"https://jalgzgoha9oynfjc.public.blob.vercel-storage.com/image/1763547757485-719604.jpeg\"\n                }\n            ],\n            \"accentColor\": \"#dc2626\",\n            \"backgroundColor\": \"#ffffff\",\n            \"textColor\": \"#1f2937\",\n            \"animationStyle\": \"fade\",\n            \"alignment\": \"left\",\n            \"overlayColor\": \"#000000\",\n            \"overlayOpacity\": 0.6,\n            \"cardHoverEffect\": \"lift\"\n        }\n    },\n    {\n        \"id\": \"dt-features-section\",\n        \"type\": \"DigitalTransformationFeatures\",\n        \"content\": {\n            \"mainTitle\": \"Digital Transformation Solutions\",\n            \"mainDescription\": \"Explore our comprehensive suite of digital transformation capabilities designed to future-proof your assets.\",\n            \"features\": [\n                {\n                    \"icon\": \"🏗️\",\n                    \"title\": \"Building Information Modeling (BIM)\",\n                    \"description\": \"BIM is central to our process—ensuring clarity, precision and alignment across all stakeholders.\",\n                    \"items\": [\n                        \"Intelligent 3D modeling\",\n                        \"Clash detection & coordination\",\n                        \"Documentation automation\",\n                        \"Lifecycle cost analysis\",\n                        \"Integrated project data environment\"\n                    ],\n                    \"backgroundImage\": \"\",\n                    \"backgroundColor\": \"#0d0d0d\",\n                    \"iconType\": \"lucide\",\n                    \"iconName\": \"Wrench\"\n                },\n                {\n                    \"icon\": \"🪄\",\n                    \"title\": \"Digital Twin Integration\",\n                    \"description\": \"A digital twin gives clients a living, breathing virtual replica of their physical asset—enabling real-time insights and predictive decision-making.\",\n                    \"items\": [\n                        \"Live monitoring of systems & performance\",\n                        \"\\\"What-if\\\" scenario simulations\",\n                        \"Predictive maintenance alerts\",\n                        \"Carbon, energy & cost optimization\",\n                        \"Safety and compliance visualization\"\n                    ],\n                    \"backgroundImage